package controller;

import models.Person;

import javax.servlet.*;
import javax.servlet.http.*;
import javax.servlet.annotation.*;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

@WebServlet(name = "PersonServlet", value = "/PersonServlet")
public class PersonServlet extends HttpServlet {
    @Override
    protected void doGet(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {

        List<Person> list = new ArrayList<>();

        Person person = new Person("Mai Văn Hoàn", "1983-08-20", "Hà Nội", "data:image/jpeg;base64,");
        Person person1 = new Person("Nguyễn Văn Nam", "1983-08-21", "Bắc Giang", "data:image/jpeg;base64,");
        Person person2 = new Person("Nguyễn Thái Hòa", "1983-08-22", "Nam Định", "data:image/jpeg;base64,");
        Person person3 = new Person("Trần Đăng Khoa", "1983-08-17", "Hà Tây", "data:image/jpeg;base64,");
        Person person4 = new Person("Nguyễn Đình Thi", "1983-08-19", "Hà Nội", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQzIH6W_EJUf41Xbne6YHzBo8MJ2m3DKsKgWw&usqp=CAU");

        list.add(person);
        list.add(person1);
        list.add(person2);
        list.add(person3);
        list.add(person4);

        request.setAttribute("list", list);
        request.getRequestDispatcher("index.jsp").forward(request, response);
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {

    }
}
